    super(props);
    this.state = {
      value: props.initialValue || '',
      focused: false,
    };

    this.changeValue = this.changeValue.bind(this);
    this.onKeyDown = this.onKeyDown.bind(this);
    this.onFocus = this.onFocus.bind(this);
    this.onBlur = this.onBlur.bind(this);
  }
  componentDidMount() {
    if (this.props.initialValue) {
      this.props.setValue(this.state.value);
    }
  }
  changeValue(event) {
    const newValue = event.currentTarget.value;

    if (this.state.value !== newValue) {
      this.setState({ value: newValue }, () => {
        if (!this.state.focused) {
          this.props.setValue(this.state.value);
        }
      });
    }
  }
  onKeyDown(e) {
    if (e.keyCode === 13) {
      this.setState({ focused: false });
      this.props.setValue(this.state.value);
    }
  }
  onFocus() {
    this.setState({ focused: true });
  }
  onBlur(e) {
    this.setState({ focused: false });
    this.props.setValue(this.state.value);

    if (this.props.onBlur) {
      this.props.onBlur(this.state.value);
    }
  }
  render() {
    const {
      children,
      name, id, value, initialValue, required,
      onChange, onFocus, onBlur,
      hasValue, getValue, setValue, resetValue,
      isValid, isValidValue, isPristine, isRequired,
      validations, setValidations, validationError, validationErrors,
      getErrorMessage, getErrorMessages, showError, showRequired,
      isFormDisabled, isFormSubmitted,
      className,
      ...otherProps } = this.props;

    return (
      <ValidationMessages
        showError={!this.state.focused && this.props.showError()}
        showRequired={!this.state.focused && this.props.showRequired()}
        isPristine={this.props.isPristine()}
        errorMessage={this.props.getErrorMessage()}
      >
        <input {...otherProps} className={className} value={this.state.value} onChange={this.changeValue} onKeyDown={this.onKeyDown} onFocus={this.onFocus} onBlur={this.onBlur} />
      </ValidationMessages>
    );
  }
}

ValidationInput.propTypes = {
  children: React.PropTypes.node,
  initialValue: React.PropTypes.string,
};

export default HOC(ValidationInput);

```

