**FormsyMaterialUIText.ts**
```typescript
import { withFormsy } from 'formsy-react';
import * as React from 'react';
import TextField from 'material-ui/TextField';

@withFormsy
export default class FormsyMaterialUIText extends React.Component<any, any> {
  constructor(props) {
    super(props);
    this.state = {
      didBlur: false,
    }
  }

  didBlur = () => {
    this.setState((prevState) => ({
      ...prevState,
      didBlur: true,
    }));
  }

  changeValue = (_, text) => {
    this.props.setValue(text);
  }

  render() {
    const { getValue, multiLine, fullWidth, getErrorMessage, floatingLabelText, floatingLabelFixed, hintText } = this.props;
    const errorMessage = this.state.didBlur && getErrorMessage();

    return (
      <TextField
        onBlur={this.didBlur}
        onChange={this.changeValue}
        value={getValue()}
        errorText={errorMessage}
        floatingLabelText={floatingLabelText}
        floatingLabelFixed={floatingLabelFixed}
        hintText={hintText}
        multiLine={multiLine}
        fullWidth={fullWidth}
      />
    )
  }
}
```

