const withXXXXX = (WrappedComponent) => {
  class HOC extends React.Component {
    render() {
      return (
        <div >
              <WrappedComponent
                {...this.props}
                ref={(ref) => {
                  this.instance = ref
                }}
              />
        </div>
      )
    }
  }
  const lifeCircles = [
    'displayName',
    'propTypes',
    'contextTypes',
    'childContextTypes',
    'mixins',
    'statics',
    'defaultProps',
    'constructor',
    'getDefaultProps',
    'state',
    'getInitialState',
    'getChildContext',
    'getDerivedStateFromProps',
    'componentWillMount',
    'UNSAFE_componentWillMount',
    'componentDidMount',
    'componentWillReceiveProps',
    'UNSAFE_componentWillReceiveProps',
    'shouldComponentUpdate',
    'componentWillUpdate',
    'UNSAFE_componentWillUpdate',
    'getSnapshotBeforeUpdate',
    'componentDidUpdate',
    'componentDidCatch',
    'componentWillUnmount',
    'render'
  ]

  Object.getOwnPropertyNames(WrappedComponent.prototype).forEach((memberName) => {
    if (lifeCircles.indexOf(memberName) >= 0) {
      return
    }
    HOC.prototype[memberName] = function (...args) {
      this.instance[memberName](...args)
    }
  })
  return HOC
}
