Here is the solution I went with:

``` js
// An AuthDriver that is safe to call blindly without going full interactive.
function InspectingAuthDriver() {
  this.firstAttempt = true;
  Dropbox.AuthDriver.Redirect.apply(this, Array.prototype.slice.call(arguments));
}
InspectingAuthDriver.prototype = Object.create(Dropbox.AuthDriver.Redirect.prototype);
InspectingAuthDriver.prototype.constructor = InspectingAuthDriver;
InspectingAuthDriver.prototype.onAuthStepChange = function(client, callback) {
  // Ignores Dropbox.Client.RESET.
  if (client.authStep === Dropbox.Client.ERROR || client.authStep >= Dropbox.Client.PARAM_LOADED) {
    // Makes subsequent attempts work first try.
    this.firstAttempt = false;
  } else if (client.authStep === Dropbox.Client.PARAM_SET && this.firstAttempt === true) {
    // Swallows the first call that would result in a redirect.
    this.firstAttempt = false;
    client.reset();
    return;
  }
  Dropbox.AuthDriver.Redirect.prototype.onAuthStepChange.call(this, client, callback);
};
