This is how I solved the problem I was trying to address, unfortunately it's only a work-around, but maybe some of you are experiencing the same problem.

```go
func merge(src interface{}, dst interface{}) error {
	srcJSON, err := json.Marshal(src)
	if err != nil {
		return err
	}
	dstJSON, err := json.Marshal(dst)
	if err != nil {
		return err
	}
	var srcm, dstm map[string]interface{}
	if err := json.Unmarshal(srcJSON, &srcm); err != nil {
		return err
	}
	if err := json.Unmarshal(dstJSON, &dstm); err != nil {
		return err
	}
	mergeMaps(srcm, dstm)

	rJSON, err := json.Marshal(dstm)
	if err != nil {
		return err
	}
	if err := json.Unmarshal(rJSON, &dst); err != nil {
		return err
	}
	return nil
}

func mergeMaps(src map[string]interface{}, dst map[string]interface{}) error {
	if (src) == nil {
		return nil
	}
	for k, v := range src {
		if v == nil {
			continue
		}
		switch v.(type) {
		case map[string]interface{}:
			if m, ok := v.(map[string]interface{}); ok {
				if md, ok := (dst)[k].(map[string]interface{}); ok {
					mergeMaps(m, md)
				}
			}
		case []interface{}:
			if a, ok := v.([]interface{}); ok {
				if ad, ok := (dst)[k].([]interface{}); ok {
					if len(a) >= len(ad) {
						for i := len(ad); i < len(a); i++ {
							ad = append(ad, a[i])
						}
						dst[k] = ad
					} else {
						for i := len(a); i < len(ad); i++ {
							a = append(a, ad[i])
						}
						dst[k] = a
					}
				} else {
					dst[k] = a
				}
			}
		default:
			ok, err := isZero(v)
			if err != nil {
				return err
			}
			if ok {
				continue
			}

			dst[k] = v
		}
	}
	return nil
}

func isZero(v interface{}) (bool, error) {
	t := reflect.TypeOf(v)
	if !t.Comparable() {
		return false, fmt.Errorf("type is not comparable: %v", t)
	}
	return v == reflect.Zero(t).Interface(), nil
}
```

