```go

package main

import (
	"encoding/json"
	"log"
	"time"

	"gopkg.in/yaml.v2"
)

const data = `
Title: Hello World
Date: 2018-10-31T00:00:00Z
Author: me
`

func main() {
	var payload struct {
		Title  string    `yaml:"Title"`
		Date   time.Time `yaml:"Date"`
		Author string    `yaml:"Author"`
	}
	if err := yaml.Unmarshal([]byte(data), &payload); err != nil {
		log.Fatal(err)
	}

