``` javascript
// copyright Isaac Z. Schlueter. Public Domain.
var glob = require('glob')
var Readable = require('stream').Readable
var util = require('util')
util.inherits(GlobStream, Readable)
function GlobStream (pattern, options) {
  Readable.call(this, options)
  var self = this
  this.glob = glob(pattern, options)
  this.glob.on('match', function (m) {
    self.push(m)
  })
  this.glob.on('end', function () {
    self.push(null)
  })
  this.glob.on('abort', function () {
    self.push(null)
  })
  this.glob.on('error', function (er) {
    self.emit('error', er)
  })
})
```

