++ (UIImageView *)defaultPlayIcon {
+    // same sizing logic with the mediaViewDisplaySize method from JSQMediaItem
+    CGSize size = CGSizeZero;
+    if ([UIDevice currentDevice].userInterfaceIdiom == UIUserInterfaceIdiomPad) {
+        size = CGSizeMake(315.0f, 225.0f);
+    }
+    else {
+        size = CGSizeMake(210.0f, 150.0f);
+    }
+    
+    UIImage *playIcon = [[UIImage jsq_defaultPlayImage] jsq_imageMaskedWithColor:[UIColor lightGrayColor]];
+    
+    UIImageView *playIconImageView = [[UIImageView alloc] initWithImage:playIcon];
+    playIconImageView.backgroundColor = [UIColor blackColor];
+    playIconImageView.frame = CGRectMake(0.0f, 0.0f, size.width, size.height);
+    playIconImageView.contentMode = UIViewContentModeCenter;
+    playIconImageView.clipsToBounds = YES;
+    
+    return playIconImageView;
+}
+
++ (UIImageView *)defaultPlayIconWithThumbnailImage:(UIImage *)thumbnailImage {
+    
+    // same sizing logic with the mediaViewDisplaySize method from JSQMediaItem
+    CGSize size = CGSizeZero;
+    if ([UIDevice currentDevice].userInterfaceIdiom == UIUserInterfaceIdiomPad) {
+        size = CGSizeMake(315.0f, 225.0f);
+    }
+    else {
+        size = CGSizeMake(210.0f, 150.0f);
+    }
+    
+    UIImageView *thumbnailImageView = [[UIImageView alloc] initWithImage:thumbnailImage];
+    thumbnailImageView.backgroundColor = [UIColor blackColor];
+    thumbnailImageView.frame = CGRectMake(0.0f, 0.0f, size.width, size.height);
+    thumbnailImageView.contentMode = UIViewContentModeCenter;
+    thumbnailImageView.clipsToBounds = YES;
+    
+    UIImageView *playIconImageView = [JSQMessagesVideoThumbnailFactory defaultPlayIcon];
+    playIconImageView.backgroundColor = [UIColor clearColor];
+    [thumbnailImageView addSubview:playIconImageView];
+    
+    return thumbnailImageView;
+}
