          mailer: {
            options: {
              key: '<%= secrets.mailgun.api_key %>',
              sender: grunt.option('from') || 'you@youremail.com',
              recipient: grunt.option('to') || 'you@youremail.com',
              subject: grunt.option('subject') || '[TEST] - Subject n/a'
