private void toggle(final boolean visible,final boolean animate,final boolean force)
  {
  if(mVisible!=visible||force)
    {
    mVisible=visible;
    final int height=getHeight();
    if(height==0&&!force)
      {
      final ViewTreeObserver vto=getViewTreeObserver();
      if(vto.isAlive())
        {
        vto.addOnPreDrawListener(new ViewTreeObserver.OnPreDrawListener()
          {
            @Override
            public boolean onPreDraw()
              {
              final ViewTreeObserver currentVto=getViewTreeObserver();
              if(currentVto.isAlive())
                currentVto.removeOnPreDrawListener(this);
              toggle(visible,animate,true);
              return true;
              }
          });
        return;
        }
      }
    final int translationY=visible ? 0 : height+getMarginBottom();
    if(animate)
      setTranslationYUsingAnimation(translationY,true);
    else setTranslationYUsingAnimation(translationY,false);
    }
  }

private void setTranslationYUsingAnimation(final int translationY,final boolean animate)
