        if navigationController.visibleViewController is MobilePlayerViewController {
            return UIInterfaceOrientationMask.Landscape
        }

        else {
            return UIInterfaceOrientationMask.Portrait
        }
    }

    return UIInterfaceOrientationMask.Portrait
