        buffer1.data = buffer2.data;
        buffer2.data = temp;
    }

    // Free buffers
    free(buffer2.data);
    free(tempBuffer);

    // Create image context from buffer
    CGContextRef ctx = CGBitmapContextCreate(buffer1.data, buffer1.width, buffer1.height,
