    name            = meta.username
    name          ||= meta.displayName
    name          ||= ""
    name            = name.replace(/[^a-z0-9]/gi, "-")
    data.firstName  = meta.name.givenName
    data.lastName   = meta.name.familyName
    data.fullName   = meta.displayName
    data.username   = meta.username
    data.username ||= name
    data.facebookId = meta.id

    @create data, callback
